script:
frappe.ui.form.on("Lead", "validate", function(frm){
  if (!isMobileNoValid(frm.doc.mobile_no)) {
    flashMessage();
 validated = false;
  }
});

function isMobileNoValid(mobno) {
   var rx = new RegExp("^[789]\\d{9}$"); 
   var isvalid = rx.test(mobno);
   return isvalid;
}

function flashMessage() {
       msgprint(__("Entered Mobile No. is either incomplete or invalid. <br /> Tip: Add it without the +0 or +91 at the beginning.", "Invalid Mobile No"));
}

frappe.ui.form.on("Lead", "source", function(frm){
    if (frm.doc.source === "IPC" || frm.doc.source === "Broker" ){
        frm.set_value("type", "Channel Partner");
    }
    else{
        frm.set_value("type", "");
    }
});

frappe.ui.form.on("Lead", "onload", function(frm) {
    frm.set_query("channel_partner", function() {
    	return {
	    "filters": {
                "partner_type": frm.doc.source
            }
        };
    });
    frm.set_query( "awfis_lead_territory", function() {
    	return {
	    "filters": {
                "is_group": "No"
            }
        };
    });
});

//Set FirstName and LastName
frappe.ui.form.on("Lead", "first_name", function(frm){
    frm.set_value("lead_name", buildName(frm.doc.first_name, frm.doc.last_name));
});

frappe.ui.form.on("Lead", "last_name", function(frm){
    frm.set_value("lead_name", buildName(frm.doc.first_name, frm.doc.last_name));
});

//Lead Name cannot be hidden via Customize Form. Hide using script.
frappe.ui.form.on("Lead", "refresh", function(frm) {
    frm.toggle_display("lead_name", false);
    frm.toggle_display("lead_name", false);
});

//Set value of Mobile No when Awfis Mobile No is entered.
frappe.ui.form.on("Lead", "awfis_mobile_no", function(frm) {
    frm.set_value("mobile_no", frm.doc.awfis_mobile_no);
});

//Set value of Mobile No when Awfis Mobile No is entered.
frappe.ui.form.on("Lead", "awfis_company_website", function(frm) {
    frm.set_value("website", frm.doc.awfis_company_website);
});

//Set value of Mobile No when Awfis Mobile No is entered.
frappe.ui.form.on("Lead", "awfis_email_id", function(frm) {
    frm.set_value("email_id", frm.doc.awfis_email_id);
});

//Set value of Territory when Awfis Lead Territory is set.
frappe.ui.form.on("Lead", "awfis_lead_territory", function(frm) {
    frm.set_value("territory", frm.doc.awfis_lead_territory);
});

//Helpers
function buildName(fname, lname) {
	if (!lname) {
		return fname;
	} else if (!fname) {
		return lname;
	} else {
		return fname + ' ' + lname;
	}
}

frappe.ui.form.on("Sales Order", "awfis_city", function(frm){
   frm.set_query("centre", function() {
     return {
	"filters": {
	   "city": frm.doc.awfis_city
        }
     };
   });
});

frappe.ui.form.on("Sales Order", "refresh", function(frm){
    frm.set_query("territory", function() {
       return {
	  "filters": {
            "parent_territory": "India"
          }
       };
    });
});

frappe.ui.form.on("Sales Order", "customer", function(frm) {
  frappe.model.with_doc("Customer", frm.doc.customer, function() { 
     var cu = frappe.model.get_doc("Customer", frm.doc.customer);

     //If a customer is not selected, do not proceed to load dependent values.
     if (!cu) { return; }

     //If a customer has no lead associated, reset existing values and return.
     if (!cu.lead_name) { 
        msgprint("This customer has no associated lead!");
        frm.set_value("awfis_lead", null);
        frm.set_value("awfis_lead_source", "");
        frm.set_value("awfis_city", null);
        frm.set_value("awfis_channel_partner", null);
        return;
      }

     frm.set_value("awfis_lead", cu.lead_name);

     frappe.model.with_doc("Lead", cu.lead_name, function() {
       var ld = frappe.model.get_doc("Lead", cu.lead_name);            
       frm.set_value("awfis_lead_source", ld.source);
       frm.set_value("awfis_city", ld.awfis_lead_territory);
     });
  });
});

frappe.ui.form.on("Sales Order", "awfis_channel_partner", function(frm){
   if (!frm.doc.awfis_channel_partner) {
      frm.set_value("sales_partner", null);
      frm.set_value("commission_rate", null);
      frm.set_value("total_commission", null);
      return;
   }
   frm.set_value("sales_partner", frm.doc.awfis_channel_partner);
   frappe.model.with_doc("Sales Partner", frm.doc.awfis_channel_partner, function() {
         var acp = frappe.model.get_doc("Sales Partner", frm.doc.awfis_channel_partner);
         frm.set_value("commission_rate", acp.commission_rate);
   });   
});

var is_mobno_valid = false;

frappe.ui.form.on("Contact", "mobile_no", function(frm){
	var mobno = frm.doc.mobile_no;
	var rx = new RegExp("^[789]\\d{9}$"); 
	var isvalid = rx.test(mobno);

	if (isvalid == true) {
          is_mobno_valid = true;
	} else {
          is_mobno_valid = false;
	  msgprint("Entered Mobile No. is either incomplete or invalid. <br /> Tip: Add it without the +0 or +91 at the beginning.", "Invalid Mobile No");
	}
});

frappe.ui.form.on("Contact", "validate", function(frm) {
   if (!is_mobno_valid) {
       msgprint("Entered Mobile No. is either incomplete or invalid. <br /> Tip: Add it without the +0 or +91 at the beginning.", "Invalid Mobile No");
      validated = false;
   }

});

frappe.ui.form.on("Opportunity", "refresh", function(frm){
	frm.set_query("awfis_lead_territory", function() {
		return {
			"filters": {
				"is_group": 0
		   	}
		};
	});
	frm.set_value("territory", frm.doc.awfis_lead_territory);
	if (frm.doc.lead && frm.is_new()) {
		load_lead_centres(frm); //Load only when form is unsaved. Once saved, centres will be fetched from database.
	}
});


frappe.ui.form.on("Opportunity", "awfis_opportunity_channel", function(frm){
	frm.set_value("source", frm.doc.awfis_opportunity_channel);
});
function load_lead_centres(frm) {
	frm.clear_table("opportunity_awfis_centres");
	frappe.call({
		method: "frappe.client.get_list",
		args: {
			doctype: "Lead Awfis Centre",
			fields: ["centre", "centre_city"],
			filters: {
				"parent": frm.doc.lead
			},
			order_by: "idx asc"
		},
		callback: function (data) {

			$.each(data.message, function(i, d) {
				var c = frm.add_child("opportunity_awfis_centres");
				c.centre = d.centre;
				c.centre_city = d.centre_city;
			});
			refresh_field("opportunity_awfis_centres");
		}
	});
}
